starterQuery:
/* Let's create a table with custom rules and default values. */

CREATE TABLE products (
    product_id INTEGER PRIMARY KEY,
    product_name TEXT NOT NULL,
    price REAL NOT NULL CHECK (price > 0),
    status TEXT DEFAULT 'in_stock' CHECK (status IN ('in_stock', 'out_of_stock', 'discontinued'))
);

theory.text:
-- Example: Creating an employees table with constraints
CREATE TABLE employees (
    emp_id INTEGER PRIMARY KEY,
    salary REAL CHECK (salary >= 30000),
    is_active INTEGER DEFAULT 1 CHECK (is_active IN (0, 1))
);
